from itertools import product


def main(word, start_letter, is_number_even):
    n = 0
    for i, w in enumerate(product(sorted(set(word)), repeat=len(word))):
        if ((i + 1) % 2 == 0) == is_number_even and w[0] == start_letter:
            n = i + 1
    return n


